const paperTitles: string[] = [
    'August 24, 2025 RSACIT Question Paper (New Syllabus)',
    'April 27, 2025 RSACIT Question Paper (New Syllabus)',
    'December 22, 2024 RSACIT Question Paper (New Syllabus)',
    'October 06, 2024 RSACIT Question Paper (New Syllabus)',
    'August 18, 2024 RSACIT Question Paper',
    'March 10, 2024 RSACIT Question Paper',
    'January 28, 2024 RSACIT Question Paper'
];

// Generate papers with specific questions based on title
export const papers: Paper[] = paperTitles.map(title => {
    let questions: QuestionFormat[] = [];
    if (title.includes('August 24, 2025')) {
        questions = august24Questions;
    } else if (title.includes('April 27, 2025')) {
        questions = april27_2025_Questions;
    } else if (title.includes('December 22, 2024')) {
        questions = december22_2024_Questions;
    } else if (title.includes('October 06, 2024')) {
        questions = october06_2024_Questions;
    } else if (title.includes('August 18, 2024')) {
        questions = august18_2024_Questions;
    } else if (title.includes('March 10, 2024')) {
        questions = march10_2024_Questions;
    } else if (title.includes('January 28, 2024')) {
        questions = january28_2024_Questions;
    }
    // Other papers will remain empty until requested.
    return {
      title: title,
      questions: questions
    };
});


const QuestionPaperViewer: React.FC<{ paper: Paper, onBack: () => void }> = ({ paper, onBack }) => {
    // Handle papers with no questions yet
    if (paper.questions.length === 0) {
        return (
             <div className="quiz-view-container" style={{background: 'var(--light-bg)'}}>
                <header className="stream-detail-header" style={{ padding: '0.75rem 1rem', background: 'var(--white)', borderBottom: '1px solid var(--border-color)', position: 'sticky', top: '0', zIndex: '5', marginBottom: 0 }}>
                    <button onClick={onBack} className="icon-button" aria-label="Go back to papers list">
                        <BackArrowIcon />
                    </button>
                    <h2 className="stream-detail-title" style={{fontSize: '1.2rem', textAlign: 'left', flexGrow: 1}}>{paper.title}</h2>
                </header>
                <div style={{ padding: '2rem', textAlign: 'center', color: 'var(--text-light)' }}>
                    <h3>Coming Soon!</h3>
                    <p>Questions for this paper will be added shortly.</p>
                </div>
            </div>
        )
    }

    return (
        <div className="quiz-view-container" style={{background: 'var(--light-bg)'}}>
             <header className="stream-detail-header" style={{ padding: '0.75rem 1rem', background: 'var(--white)', borderBottom: '1px solid var(--border-color)', position: 'sticky', top: '0', zIndex: '5', marginBottom: 0 }}>
                <button onClick={onBack} className="icon-button" aria-label="Go back to papers list">
                    <BackArrowIcon />
                </button>
                <h2 className="stream-detail-title" style={{fontSize: '1.2rem', textAlign: 'left', flexGrow: 1}}>{paper.title}</h2>
            </header>
            <div className="question-paper-view">
                {paper.questions.map((q, index) => (
                    <div key={index} className="question-paper-block">
                        <p className="question-paper-text">{index + 1}. {q.text.split('\n').map((line, i) => <React.Fragment key={i}>{line}<br/></React.Fragment>)}</p>
                        <ul className="question-paper-options">
                            {q.options.map((option, oIndex) => {
                                const isCorrect = q.answer === option;
                                return (
                                    <li key={oIndex} className={`question-paper-option ${isCorrect ? 'correct-option' : ''}`}>
                                        <div className="option-indicator">
                                            {isCorrect && <CheckIcon className="check-icon" />}
                                        </div>
                                        <span>{option.replace(/\s+/g, ' ').trim()}</span>
                                    </li>
                                );
                            })}
                        </ul>
                        <div className="question-paper-counter">
                           {index + 1} / {paper.questions.length}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};


interface OldPapersViewProps {
    streamName: string;
    onBack: () => void;
}

const OldPapersView: React.FC<OldPapersViewProps> = ({ streamName, onBack }) => {
    const [selectedPaper, setSelectedPaper] = useState<Paper | null>(null);

    if (selectedPaper) {
        return <QuestionPaperViewer paper={selectedPaper} onBack={() => setSelectedPaper(null)} />;
    }

    return (
         <div className="stream-detail-container">
            <header className="stream-detail-header">
                <button onClick={onBack} className="icon-button" aria-label="Go back">
                    <BackArrowIcon />
                </button>
                <h2 className="stream-detail-title">Old Papers for {streamName}</h2>
            </header>
            <main className="category-container">
                {papers.map((paper) => {
                    const hasQuestions = paper.questions.length > 0;
                    return (
                        <div 
                            key={paper.title} 
                            className="category-tile" 
                            onClick={() => hasQuestions && setSelectedPaper(paper)} 
                            style={{
                                cursor: hasQuestions ? 'pointer' : 'not-allowed',
                                opacity: hasQuestions ? 1 : 0.6
                            }}
                        >
                            <div className="category-info">
                                <OldPaperIcon />
                                <h3 className="category-tile-title" style={{fontSize: '1rem'}}>{paper.title}</h3>
                            </div>
                            <span className="category-item-count">
                                {paper.questions.length}
                            </span>
                        </div>
                    );
                })}
            </main>
        </div>
    );
};

export default OldPapersView;